import styled from 'styled-components';

// import { Link } from 'react-router-dom';


export const FooterContainer = styled.div`
    height: 90px;
    background-color:#8E8E93;
    border-top: 1px solid #999;
    width: 100%;
    padding-top: 0px;
    bottom: 0;
    display: flex;
    flex-direction: row;
    text-align: center;
  	justify-content: space-evenly;
    margin: auto;
    margin-bottom: -30px;
    position: relative;
  	@media screen and (max-width: 800px) {
        padding: 10px;
        height: 300px;
        flex-direction: column;
    	font-size: 10px;
  	}
`;

export const LinkOptions = styled.div`
  	width: 100%;
    position: relative;
    top: 0;
    color: white;
    font-weight: 500;
    vertical-align: top;
    flex-direction: column;
  	display: flex;
    align-items: center;
    padding: 15px;
  	@media screen and (max-width: 800px) {
        width: 100%;
    	margin-top: -25px;
  	}
`;
export const LinkOptionsOption = styled.div`
    position: relative;
    width: 100%;
    color: #555555;
    float: left;
    font-weight: 300;
    text-align: center;
    display: flex;
    justify-content: space-evenly;
  	align-items: center;
  	@media screen and (max-width: 800px) {
        width: 50%;
        text-align: left;
  	}
`;
