import React from 'react';
import FormInput from '../form-input/form-input.component';
import CustomButton from '../buttons/buttons.component';
import './sign-up.styles.scss';
import { auth, createUserProfileDocument } from '../../firebase/firebase.utils';

class SignUp extends React.Component {
    constructor(){
        super();
        this.state = {
            displayName: '',
            email: '',
            password: '',
            confirmPassword: ''
        }
    }

    handleSubmit = async event => {
        event.preventDefault();
        const { email, displayName, password, confirmPassword } = this.state;
        if  (password !== confirmPassword ) {
            alert("passwords don't match");
            return;
        }
        try {
            const { user } = await auth.createUserWithEmailAndPassword(
          email,
          password
            );
        await createUserProfileDocument(user, { displayName });
            this.setState({
            displayName: '',
            email: '',
            password: '',
            confirmPassword: ''
            });
        } 
        catch (error) {
            console.error(error);
        }
    };

    handleChange = event => {
      const { name, value } = event.target;
      this.setState({ [name]: value });
    };

    render(){
        const { email, displayName, password, confirmPassword } = this.state;
        return(
            <div className="sign-up">
                <head>
					<meta charset="UTF-8" />
					<meta name="viewport" content="width=device-width, initial-scale=1" />
					<title>Coderality Sign In</title>
					<meta name="description" content="Sign up with a free account where you can stay up to date with our latest blog posts." />		
				</head>
                <h2 className="title"><b>Don't have an account yet?</b> </h2>
                <span className="title">Sign up with your email</span>
                <form className="sign-up-form" onSubmit={this.handleSubmit}>
                    <FormInput
                        type="text"
                        name="displayName"
                        value={displayName}
                        onChange={this.handleChange}
                        label="Display Name"
                        required
                        />

                    <FormInput
                        type="email"
                        name="email"
                        value={email}
                        onChange={this.handleChange}
                        label="Email"
                        required
                        />

                    <FormInput
                        type="password"
                        name="password"
                        value={password}
                        onChange={this.handleChange}
                        label="Password"
                        required
                        />

                    <FormInput
                        type="password"
                        name="confirmPassword"
                        value={confirmPassword}
                        onChange={this.handleChange}
                        label="Confirm Password"
                        required
                        />
                   <CustomButton type='submit'>SIGN UP</CustomButton>
                </form>
            </div>
        )
    }
}

export default SignUp;
