import styled from 'styled-components';
import { Link } from 'react-router-dom';


export const FooterContainer = styled.div`
  	height: 100px;
    width: 100%;
    bottom: 0;
    display: flex;
    flex-direction: row;
    text-align: center;
  	justify-content: space-evenly;
    margin: auto;
    position: relative;
    background-color: white !important;
  	@media screen and (max-width: 800px) {
        padding: 10px;
        flex-direction: column;
    	font-size: 10px;
  	}
`;

export const LinkOptions = styled.div`
  	width: 100%;
    background-color: white !important;
    position: relative;
    text-align: center;
    flex-direction: column;
  	display: flex;
  	align-items: center;
  	margin-top: -50px;
  	justify-content: center;
  	@media screen and (max-width: 800px) {
    	width: 100%;
    	margin-top: -25px;
  	}
`;
export const LinkOptionsOption = styled.div`
    background-color: white !important;
    position: relative;
    text-align: center;
    height: auto;
    display: flex;
  	align-items: center;
  	@media screen and (max-width: 800px) {
    	width: 100%;
  	}
`;
