import React from 'react';
import BlogItem from '../blog-collection-item/blog-collection-item.component';
import '../../components/blog-preview/blog-preview.styles.jsx';

import {
	CollectionPreview
} from '../blog-preview/blog-preview.styles';

const CategoryFilter = ({ category }) => (	
	<CollectionPreview className='collection-preview'>	
	    <div className="blog-category-container">
			<div className="blog-categories">
				<div className="cat">
				{category.map(category => <li>{category}</li>)} 
				</div>
			</div>
		</div>
	</CollectionPreview>
);
export default CategoryFilter;
