import React from 'react';
import {Helmet} from 'react-helmet';
import SHOP_DATA from './shop.data.js';
import CollectionPreview from '../../components/collection-preview/collection-preview.component';

class ShopPage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      collections: SHOP_DATA
    };
  }

  render() {
    const { collections } = this.state;
    return (
      <div className='shop-page'>
         <div className='shop-container'>
          <div class="shop-text-wrapper">
        <head>
        <Helmet>
            <meta charset="UTF-8" />
            <meta name="viewport" content="width=device-width, initial-scale=1" />
            <title>Coderality Clothing</title>
            <meta name="description" content="Find the coolest programming t-shirts. We sell shirts in many sizes and colors." />
        </Helmet>
         </head> 

        <Helmet>
            <title>About Coderality</title>
            <meta name="description" content="We hope to inspire people of all ages to have a code mentality." />
        </Helmet>
            <div className="intro-container-2">
                {/* <img src="https://i.postimg.cc/sX7G7qwt/shop-page.png" alt="computer" /> */}
            </div>
         <div className="shop-container">

            {collections.map(({ id, ...otherCollectionProps }) => (
          <CollectionPreview key={id} {...otherCollectionProps} />
        ))}
         </div>
       </div>
       </div>
      </div>
    );
  }
}

export default ShopPage;
